import logo from './logo.svg';
import { Component } from 'react';
import './App.css';

class App extends Component{
  constructor ()
  {
    super();

    this.state = {
      monsters:[],
    }
  }


componentDidMount()
{ 
  fetch('https://jsonplaceholder.typicode.com/users')
    .then((event)=> event.json())
    .then((req)=> this.setState(
      ()=>
      {
        return {monsters:req}
      }
      ,()=>
      {
        console.log(this.state)
      }));
}

SeachMethod = (event)=>
{
  Result = this.state.monsters.filter((monster)=>
  {
    return monster.name.includes(event.target.value);
  })
  return Result;
}

  render()
  {
    return (
      <div className="App">

        <div className='SearchBar'>
          <input className='SearchInput' onChange={this.SeachMethod}/>
        </div>

        <div>
            {
              this.state.monsters.map((monster)=>{
              return (
                <div key={monster.id}>

                  <h2>
                    {monster.name}
                  </h2>
                </div>
              );
            }
              )
            }
        </div>
      </div>
    );
  };

}

export default App;
