import logo from './logo.svg';
import { Component } from 'react';
import './App.css';

class App extends Component{
  constructor ()
  {
    super();

    this.state = {
      monsters:[],
      _onChangeValue:'',

    }
  }


componentDidMount()
{ 
  fetch('https://jsonplaceholder.typicode.com/users')
    .then((event)=> event.json())
    .then((req)=> this.setState(
      ()=>
      {
        return {monsters:req}
      }
      ,()=>
      {
        console.log(this.state)
      }));
}

SeachMethod = (event)=>
{
  const _result = event.target.value.toLocaleLowerCase();
  this.setState(()=>
  {
    return{_onChangeValue:_result};
  });
};

  render()
  {
    const filteredMonsters = this.state.monsters.filter((monster)=> 
    {
      return monster.name.toLocaleLowerCase.includes(this.state._onChangeValue);
    });



    return (
      <div className="App">

        <div className='SearchBar'>
          <input className='SearchInput' placeholder='Monster Name ' onChange={this.SeachMethod}/>
        </div>

        <div>
            {
              filteredMonsters.map((monster)=>{
              return (
                <div key={monster.id}>

                  <h2>
                    {monster.name}
                  </h2>
                </div>
              );
            }
              )
            }
        </div>
      </div>
    );
  };

}

export default App;
